contracts/actions/ZapOut.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.7.6;
pragma abicoder v2;

import '@uniswap/v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol';
import '@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol';
import '../helpers/UniswapNFTHelper.sol';
import '../helpers/ERC20Helper.sol';
import '../utils/Storage.sol';
import '../../interfaces/IPositionManager.sol';
import '../../interfaces/actions/IZapOut.sol';

///@notice ZapOut allows users to close positions and withdraw to a single output token
contract ZapOut is IZapOut {
    ///@notice emitted when a UniswapNFT is zapped out
    ///@param positionManager address of PositionManager
    ///@param tokenId Id of zapped token
    ///@param tokenOut address of token zapped out
    ///@param amountOut amount of tokenOut zapped out
    event ZappedOut(address indexed positionManager, uint256 tokenId, address tokenOut, uint256 amountOut);

    ///@notice burns a uni NFT with a single output token, the output token can be different from the two position tokens
    ///@param tokenId id of the NFT to burn
    ///@param tokenOut address of output token
    ///@return uint256 amount of tokenOut withdrawn
    function zapOut(uint256 tokenId, address tokenOut) public override returns (uint256) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        INonfungiblePositionManager nonfungiblePositionManager = INonfungiblePositionManager(
            Storage.uniswapAddressHolder.nonfungiblePositionManagerAddress()
        );

        (address token0, address token1, , , ) = UniswapNFTHelper._getTokens(tokenId, nonfungiblePositionManager);

        (, , , , , , , uint128 liquidity, , , , ) = nonfungiblePositionManager.positions(tokenId);

        nonfungiblePositionManager.decreaseLiquidity(
            INonfungiblePositionManager.DecreaseLiquidityParams({
                tokenId: tokenId,
                liquidity: liquidity,
                amount0Min: 0,
                amount1Min: 0,
                deadline: block.timestamp + 120
            })
        );

        (uint256 amount0, uint256 amount1) = nonfungiblePositionManager.collect(
            INonfungiblePositionManager.CollectParams({
                tokenId: tokenId,
                recipient: address(this),
                amount0Max: 2**128 - 1,
                amount1Max: 2**128 - 1
            })
        );

        nonfungiblePositionManager.burn(tokenId);
        IPositionManager(address(this)).removePositionId(tokenId);

        if (tokenOut != token0) {
            amount0 = _swapToTokenOut(tokenOut, token0, amount0);
        }

        if (tokenOut != token1) {
            amount1 = _swapToTokenOut(tokenOut, token1, amount1);
        }

        ERC20Helper._approveToken(tokenOut, address(this), amount0 + amount1);
        ERC20Helper._withdrawTokens(tokenOut, Storage.owner, amount0 + amount1);

        emit ZappedOut(address(this), tokenId, tokenOut, amount0 + amount1);
        return amount0 + amount1;
    }

    ///@notice performs the swap to tokenOut
    ///@param tokenOut address of output token
    ///@param tokenIn address of input token
    ///@param amountIn amount of input token
    ///@return amountOut of output token
    function _swapToTokenOut(
        address tokenOut,
        address tokenIn,
        uint256 amountIn
    ) internal returns (uint256 amountOut) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();

        ERC20Helper._approveToken(tokenIn, Storage.uniswapAddressHolder.swapRouterAddress(), amountIn);

        ISwapRouter swapRouter = ISwapRouter(Storage.uniswapAddressHolder.swapRouterAddress());
        amountOut = swapRouter.exactInputSingle(
            ISwapRouter.ExactInputSingleParams({
                tokenIn: tokenIn,
                tokenOut: tokenOut,
                fee: _findBestFee(tokenIn, tokenOut),
                recipient: address(this),
                deadline: block.timestamp + 120,
                amountIn: amountIn,
                amountOutMinimum: 1,
                sqrtPriceLimitX96: 0
            })
        );
    }

    ///@notice finds the best fee tier on which to perform a swap
    ///@param token0 address of first token
    ///@param token1 address of second token
    ///@return fee suggested fee tier
    function _findBestFee(address token0, address token1) internal view returns (uint24 fee) {
        uint128 bestLiquidity = 0;
        uint16[4] memory fees = [100, 500, 3000, 10000];

        for (uint8 i = 0; i < 4; i++) {
            try this.getPoolLiquidity(token0, token1, uint24(fees[i])) returns (uint128 nextLiquidity) {
                if (nextLiquidity > bestLiquidity) {
                    bestLiquidity = nextLiquidity;
                    fee = fees[i];
                }
            } catch {
                //pass
            }
        }

        if (bestLiquidity == 0) {
            revert('ZapOut::_findBestFee: No pool found with desired tokens');
        }
    }

    ///@notice wrapper of getPoolLiquidity to use try/catch statement
    ///@param token0 address of first token
    ///@param token1 address of second token
    ///@param fee pool fee tier
    ///@return liquidity of the pool
    function getPoolLiquidity(
        address token0,
        address token1,
        uint24 fee
    ) public view returns (uint128 liquidity) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        return
            IUniswapV3Pool(
                UniswapNFTHelper._getPool(Storage.uniswapAddressHolder.uniswapV3FactoryAddress(), token0, token1, fee)
            ).liquidity();
    }
}

contracts/utils/DepositRecipes.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.7.6;
pragma abicoder v2;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '@uniswap/v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol';
import '../../interfaces/actions/IMint.sol';
import '../../interfaces/actions/IZapIn.sol';
import '../../interfaces/IPositionManager.sol';
import '../../interfaces/IPositionManagerFactory.sol';
import '../../interfaces/IUniswapAddressHolder.sol';

///@notice DepositRecipes allows user to fill their position manager with UniswapV3 positions
///        by depositing an already minted NFT or by minting directly a new one
contract DepositRecipes {
    IUniswapAddressHolder uniswapAddressHolder;
    IPositionManagerFactory positionManagerFactory;

    constructor(address _uniswapAddressHolder, address _positionManagerFactory) {
        uniswapAddressHolder = IUniswapAddressHolder(_uniswapAddressHolder);
        positionManagerFactory = IPositionManagerFactory(_positionManagerFactory);
    }

    ///@notice emitted when a position is created
    ///@param positionManager the address of the position manager which recieved the position
    ///@param from address of the user
    ///@param tokenId ID of the minted NFT
    event PositionDeposited(address indexed positionManager, address from, uint256 tokenId);

    ///@notice add uniswap position NFT to the position manager
    ///@param tokenIds IDs of deposited tokens
    function depositUniNft(uint256[] calldata tokenIds) external {
        address positionManagerAddress = positionManagerFactory.userToPositionManager(msg.sender);

        for (uint32 i = 0; i < tokenIds.length; i++) {
            INonfungiblePositionManager(uniswapAddressHolder.nonfungiblePositionManagerAddress()).safeTransferFrom(
                msg.sender,
                positionManagerAddress,
                tokenIds[i],
                '0x0'
            );
            IPositionManager(positionManagerAddress).pushPositionId(tokenIds[i]);
            emit PositionDeposited(positionManagerAddress, msg.sender, tokenIds[i]);
        }
    }

    ///@notice mint uniswapV3 NFT and deposit in the position manager
    ///@param token0 the first token to be deposited
    ///@param token1 the second token to be deposited
    ///@param fee fee tier of the pool to be deposited in
    ///@param tickLower the lower bound of the position range
    ///@param tickUpper the upper bound of the position range
    ///@param amount0Desired the amount of the first token to be deposited
    ///@param amount1Desired the amount of the second token to be deposited
    ///@return tokenId the ID of the minted NFT
    function mintAndDeposit(
        address token0,
        address token1,
        uint24 fee,
        int24 tickLower,
        int24 tickUpper,
        uint256 amount0Desired,
        uint256 amount1Desired
    ) external returns (uint256 tokenId) {
        address positionManagerAddress = positionManagerFactory.userToPositionManager(msg.sender);

        ///@dev send tokens to position manager to be able to call the mint action
        IERC20(token0).transferFrom(msg.sender, positionManagerAddress, amount0Desired);
        IERC20(token1).transferFrom(msg.sender, positionManagerAddress, amount1Desired);

        (tokenId, , ) = IMint(positionManagerAddress).mint(
            IMint.MintInput(token0, token1, fee, tickLower, tickUpper, amount0Desired, amount1Desired)
        );
    }

    ///@notice mints a uni NFT with a single input token, the token in input can be different from the two position tokens
    ///@param tokenIn address of input token
    ///@param amountIn amount of input token
    ///@param token0 address token0 of the pool
    ///@param token1 address token1 of the pool
    ///@param tickLower lower bound of desired position
    ///@param tickUpper upper bound of desired position
    ///@param fee fee tier of the pool
    ///@return tokenId of minted NFT
    function zapInUniNft(
        address tokenIn,
        uint256 amountIn,
        address token0,
        address token1,
        int24 tickLower,
        int24 tickUpper,
        uint24 fee
    ) external returns (uint256 tokenId) {
        address positionManagerAddress = positionManagerFactory.userToPositionManager(msg.sender);

        (tokenId) = IZapIn(positionManagerAddress).zapIn(tokenIn, amountIn, token0, token1, tickLower, tickUpper, fee);
        IPositionManager(positionManagerAddress).pushPositionId(tokenId);
    }
}

