contracts/actions/IncreaseLiquidity.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.7.6;
pragma abicoder v2;

import '@uniswap/v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol';
import '../helpers/ERC20Helper.sol';
import '../helpers/UniswapNFTHelper.sol';
import '../utils/Storage.sol';
import '../../interfaces/actions/IIncreaseLiquidity.sol';

///@notice action to increase the liquidity of a V3 position
contract IncreaseLiquidity is IIncreaseLiquidity {
    ///@notice emitted when liquidity is increased
    ///@param positionManager address of the position manager which increased liquidity
    ///@param tokenId id of the position
    event LiquidityIncreased(address indexed positionManager, uint256 tokenId);

    ///@notice increase the liquidity of a UniswapV3 position
    ///@param tokenId the id of the position token
    ///@param amount0Desired the desired amount of token0
    ///@param amount1Desired the desired amount of token1
    function increaseLiquidity(
        uint256 tokenId,
        uint256 amount0Desired,
        uint256 amount1Desired
    ) public override {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();

        require(
            amount0Desired > 0 || amount1Desired > 0,
            'IncreaseLiquidity::increaseLiquidity: Amounts cannot be both zero'
        );

        (address token0Address, address token1Address, , , ) = UniswapNFTHelper._getTokens(
            tokenId,
            INonfungiblePositionManager(Storage.uniswapAddressHolder.nonfungiblePositionManagerAddress())
        );

        ERC20Helper._approveToken(
            token0Address,
            Storage.uniswapAddressHolder.nonfungiblePositionManagerAddress(),
            amount0Desired
        );
        ERC20Helper._approveToken(
            token1Address,
            Storage.uniswapAddressHolder.nonfungiblePositionManagerAddress(),
            amount1Desired
        );

        INonfungiblePositionManager.IncreaseLiquidityParams memory params = INonfungiblePositionManager
            .IncreaseLiquidityParams({
                tokenId: tokenId,
                amount0Desired: amount0Desired,
                amount1Desired: amount1Desired,
                amount0Min: 0,
                amount1Min: 0,
                deadline: block.timestamp + 120
            });
        INonfungiblePositionManager(Storage.uniswapAddressHolder.nonfungiblePositionManagerAddress()).increaseLiquidity(
                params
            );

        emit LiquidityIncreased(address(this), tokenId);
    }
}

contracts/actions/Swap.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.7.6;
pragma abicoder v2;

import '@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol';
import '../helpers/ERC20Helper.sol';
import '../utils/Storage.sol';
import '../../interfaces/actions/ISwap.sol';

contract Swap is ISwap {
    ///@notice emitted when a swap is performed
    ///@param positionManager address of the position manager which performed the swap
    ///@param tokenIn address of the token being swapped in
    ///@param tokenOut address of the token being swapped out
    ///@param amountIn amount of the token being swapped in
    ///@param amountOut amount of the token being swapped out
    event Swapped(
        address indexed positionManager,
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 amountOut
    );

    ///@notice swaps token0 for token1 on uniswap
    ///@param token0Address address of first token
    ///@param token1Address address of second token
    ///@param fee fee tier of the pool
    ///@param amount0In amount of token0 to swap
    function swap(
        address token0Address,
        address token1Address,
        uint24 fee,
        uint256 amount0In
    ) public override returns (uint256 amount1Out) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        ISwapRouter swapRouter = ISwapRouter(Storage.uniswapAddressHolder.swapRouterAddress());

        ERC20Helper._approveToken(token0Address, address(swapRouter), 2**256 - 1);
        ERC20Helper._approveToken(token1Address, address(swapRouter), 2**256 - 1);

        ISwapRouter.ExactInputSingleParams memory swapParams = ISwapRouter.ExactInputSingleParams({
            tokenIn: token0Address,
            tokenOut: token1Address,
            fee: fee,
            recipient: address(this),
            deadline: block.timestamp + 120,
            amountIn: amount0In,
            amountOutMinimum: 0,
            sqrtPriceLimitX96: 0
        });

        amount1Out = swapRouter.exactInputSingle(swapParams);
        emit Swapped(address(this), token0Address, token1Address, amount0In, amount1Out);
    }
}

contracts/actions/CollectFees.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.7.6;
pragma abicoder v2;

import '@uniswap/v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol';
import '../utils/Storage.sol';
import '../../interfaces/actions/ICollectFees.sol';

///@notice collect fees from a uniswapV3 position
contract CollectFees is ICollectFees {
    ///@notice emitted upon collect fees of a UniswapV3 position
    ///@param positionManager address of the position manager which collected fees
    ///@param tokenId id of the position
    ///@param amount0 amount of token0 collected
    ///@param amount1 amount of token1 collected
    event FeesCollected(address indexed positionManager, uint256 tokenId, uint256 amount0, uint256 amount1);

    ///@notice collect fees from a uniswapV3 position
    ///@param tokenId of token to collect fees from
    ///@param returnTokensToUser whether or not to return the collected fees to the user
    ///@return amount0 of token0 collected
    ///@return amount1 of token1 collected
    function collectFees(uint256 tokenId, bool returnTokensToUser)
        public
        override
        returns (uint256 amount0, uint256 amount1)
    {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();

        _updateUncollectedFees(tokenId);

        INonfungiblePositionManager nonfungiblePositionManager = INonfungiblePositionManager(
            Storage.uniswapAddressHolder.nonfungiblePositionManagerAddress()
        );
        (, , , , , , , , , , uint128 feesToken0, uint128 feesToken1) = nonfungiblePositionManager.positions(tokenId);

        INonfungiblePositionManager.CollectParams memory params = INonfungiblePositionManager.CollectParams({
            tokenId: tokenId,
            recipient: returnTokensToUser ? Storage.owner : address(this),
            amount0Max: feesToken0,
            amount1Max: feesToken1
        });

        (amount0, amount1) = nonfungiblePositionManager.collect(params);
        emit FeesCollected(address(this), tokenId, amount0, amount1);
    }

    ///@notice update the uncollected fees of a uniswapV3 position
    ///@param tokenId ID of the token to check fees from
    function _updateUncollectedFees(uint256 tokenId) internal {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        INonfungiblePositionManager.DecreaseLiquidityParams memory params = INonfungiblePositionManager
            .DecreaseLiquidityParams({
                tokenId: tokenId,
                liquidity: 1,
                amount0Min: 0,
                amount1Min: 0,
                deadline: block.timestamp + 120
            });
        INonfungiblePositionManager(Storage.uniswapAddressHolder.nonfungiblePositionManagerAddress()).decreaseLiquidity(
                params
            );
    }
}

contracts/actions/DecreaseLiquidity.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.7.6;
pragma abicoder v2;

import '@uniswap/v3-core/contracts/libraries/TickMath.sol';
import '@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol';
import '@uniswap/v3-periphery/contracts/libraries/LiquidityAmounts.sol';
import '@uniswap/v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol';
import '../helpers/UniswapNFTHelper.sol';
import '../utils/Storage.sol';
import '../../interfaces/actions/IDecreaseLiquidity.sol';

///@notice action to decrease liquidity of an NFT position
contract DecreaseLiquidity is IDecreaseLiquidity {
    ///@notice emitted when liquidity is decreased
    ///@param positionManager address of the position manager which decreased liquidity
    ///@param tokenId id of the position
    event LiquidityDecreased(address indexed positionManager, uint256 tokenId);

    ///@notice decrease the liquidity of a V3 position
    ///@param tokenId the tokenId of the position
    ///@param amount0Desired the amount of token0 liquidity desired
    ///@param amount1Desired the amount of token1 liquidity desired
    ///@return liquidityToDecrease the amount of liquidity to decrease
    ///@return amount0 the amount of token0 removed
    ///@return amount1 the amount of token1 removed
    function decreaseLiquidity(
        uint256 tokenId,
        uint256 amount0Desired,
        uint256 amount1Desired
    )
        public
        override
        returns (
            uint128 liquidityToDecrease,
            uint256 amount0,
            uint256 amount1
        )
    {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();

        (, , , , , int24 tickLower, int24 tickUpper, uint128 liquidity, , , , ) = INonfungiblePositionManager(
            Storage.uniswapAddressHolder.nonfungiblePositionManagerAddress()
        ).positions(tokenId);

        IUniswapV3Pool pool = IUniswapV3Pool(
            UniswapNFTHelper._getPoolFromTokenId(
                tokenId,
                INonfungiblePositionManager(Storage.uniswapAddressHolder.nonfungiblePositionManagerAddress()),
                Storage.uniswapAddressHolder.uniswapV3FactoryAddress()
            )
        );

        (uint160 sqrtRatioX96, , , , , , ) = pool.slot0();

        liquidityToDecrease = LiquidityAmounts.getLiquidityForAmounts(
            sqrtRatioX96,
            TickMath.getSqrtRatioAtTick(tickLower),
            TickMath.getSqrtRatioAtTick(tickUpper),
            amount0Desired,
            amount1Desired
        );

        ///@dev remove all liquidity if the amount to decrease is greater than the amount in the pool
        if (liquidityToDecrease > liquidity) {
            liquidityToDecrease = liquidity;
        }

        INonfungiblePositionManager.DecreaseLiquidityParams memory decreaseliquidityparams = INonfungiblePositionManager
            .DecreaseLiquidityParams({
                tokenId: tokenId,
                liquidity: liquidityToDecrease,
                amount0Min: 0,
                amount1Min: 0,
                deadline: block.timestamp + 120
            });

        (amount0, amount1) = INonfungiblePositionManager(
            Storage.uniswapAddressHolder.nonfungiblePositionManagerAddress()
        ).decreaseLiquidity(decreaseliquidityparams);

        emit LiquidityDecreased(address(this), tokenId);
    }
}

contracts/actions/AaveWithdraw.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.7.6;
pragma abicoder v2;

import '../../interfaces/IAToken.sol';
import '../../interfaces/ILendingPool.sol';
import '../../interfaces/IPositionManager.sol';
import '../../interfaces/actions/IAaveWithdraw.sol';
import '../utils/Storage.sol';

///@notice action to withdraw tokens from aave protocol
contract AaveWithdraw is IAaveWithdraw {
    ///@notice emitted when a withdraw from aave is made
    ///@param positionManager address of aave positionManager which withdrew
    ///@param token token address
    ///@param amount amount withdrawn
    event WithdrawnFromAave(address indexed positionManager, address token, uint256 amount);

    ///@notice withdraw from aave some token amount
    ///@param token token address
    ///@param id position to withdraw from
    ///@return amountWithdrawn amount of token withdrawn from aave
    function withdrawFromAave(address token, uint256 id) public override returns (uint256 amountWithdrawn) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        require(
            Storage.aaveUserReserves[token].positionShares[id] > 0,
            'PositionManager::removeAavePosition: no position to withdraw!'
        );

        uint256 amount = _getAmount(token, id);

        amountWithdrawn = ILendingPool(Storage.aaveAddressHolder.lendingPoolAddress()).withdraw(
            token,
            amount,
            address(this)
        );

        _removeAavePosition(token, id);
        emit WithdrawnFromAave(address(this), token, amountWithdrawn);
    }

    function _getAmount(address token, uint256 id) internal view returns (uint256 amount) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        IAToken aToken = IAToken(
            ILendingPool(PositionManagerStorage.getStorage().aaveAddressHolder.lendingPoolAddress())
                .getReserveData(token)
                .aTokenAddress
        );

        amount =
            (aToken.balanceOf(address(this)) * Storage.aaveUserReserves[token].positionShares[id]) /
            Storage.aaveUserReserves[token].sharesEmitted;
    }

    ///@notice remove awareness of aave position from positionManager
    ///@param token address of token withdrawn
    ///@param id of the withdrawn position
    function _removeAavePosition(address token, uint256 id) internal {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();

        Storage.aaveUserReserves[token].sharesEmitted -= Storage.aaveUserReserves[token].positionShares[id];
        Storage.aaveUserReserves[token].positionShares[id] = 0;
        Storage.aaveUserReserves[token].tokenIds[id] = 0;
    }
}

